/**
 * 将任意两个按键作为热键（默认：Enter）
 * Use any two keys as hotkey (default: Enter)
 * @param {HTMLElement} element - 监听的 DOM 元素
 * @param {object} [options] - 配置选项
 * @param {number} [options.key] - 主按键的键代码，默认为 13（Enter 键）
 * @param {number} [options.subKey] - 副按键的键代码
 * @param {function} [options.onKey] - 主按键按下时触发的回调函数
 * @param {function} [options.onHotkey] - 主副按键按下时触发的回调函数
 * @param {number} [options.interval] - 主副按键按下的最大时间间隔（毫秒），默认为 250ms
 */
function AnyHotKey(e,n){let t={key:13,subKey:0,onKey:function(){},onHotkey:function(){},interval:250};let o=Object.assign({},t,n);let y=[0,0];if(o.subKey===0){e.addEventListener("keydown",function(e){const n=e.keyCode;if(n===o.key){if(n===13){e.preventDefault?e.preventDefault():e.returnValue=false}o.onKey(this)}})}else{e.addEventListener("keydown",function(e){const n=e.keyCode;const t=(new Date).getTime();if(n===o.key){if(n===13){e.preventDefault?e.preventDefault():e.returnValue=false}if(y[0]===o.subKey&&y[1]+o.interval>=t){o.onHotkey(this)}else{o.onKey(this)}}y[0]=n;y[1]=t})}}if(typeof module!=="undefined"&&typeof module.exports!=="undefined"){module.exports={AnyHotKey:AnyHotKey}}else{window.AnyHotKey=AnyHotKey}